import React, { useContext } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faClose } from '@fortawesome/free-solid-svg-icons';
import { useCart } from '../contexts/CartContext';

function CartModal(props) {
    const context = useContext(useCart)
  return (
    <div className= 'fixed z-auto top-1 right-1 h-[100vh] overflow-scroll w-[300px] bg-slate-900' style={ {display: `${props.style}`}}>
        <div className='flex justify-between mt-2'>
            <h2 className='text-white text-[30px]'>Cart</h2>
            <button onClick={()=>context.setshowCartModal(!context.showCartModal)}><FontAwesomeIcon icon={faClose} className='text-white text-2xl ' /></button>
        </div>
        <div className='my-3 '>
            <ul>
                <li className='flex gap-4'></li>
            </ul>
        </div>
    </div>
  )
}

export default CartModal
