import React, { useContext } from 'react'
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import { useCart } from '../contexts/CartContext';



function Cards(props) {
    const context = useContext(useCart)

    const toAddCart = ()=>{
        context.setItems([...context.Items  ,{
            product
        }])

    }
  return (
    <Card style={{ width: '18rem' , height: '400px' , marginTop:'10px'}}  >
    <Card.Img variant="top" src={props.image}  style={{height:'200px'}}/>
    <Card.Body>
      <Card.Title>{props.title}</Card.Title>
      <Card.Text>
      </Card.Text>
      <div className='flex flex-wrap items-start justify-between'>
      <Button variant="primary"> price $ :{props.price}</Button>
      <button className='bg-yellow-500 text-white rounded-md p-2 text-[17px]' onClick={toAddCart}>Add To Cart </button>
      </div>
    </Card.Body>
  </Card>
  )
}

export default Cards
