import React, { useContext } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faClose } from '@fortawesome/free-solid-svg-icons';
import { useCart } from '../contexts/CartContext';

function CartModal(props) {
    const context = useContext(useCart)
  return (
    <div className= {`fixed  top-1 right-1 h-[100vh] overflow-scroll w-[300px] bg-slate-900 ${props.class}`}>
        <div className='flex justify-between mt-2'>
            <h2 className='text-white text-[30px]'>Cart</h2>
            <button onClick={()=>context.setshowCartModal(!context.showCartModa)}><FontAwesomeIcon icon={faClose} className='text-white text-2xl ' /></button>
        </div>
    </div>
  )
}

export default CartModal
