import React ,{createContext , useState} from 'react'

export const useCart = createContext()


export const UseCartContext = (props)=>{

    const [Items, setItems] = useState([])
    const [, set] = useState([])
    
    return(
        <useCart.Provider value={{Items , setItems}}>
        {props.children}
        </useCart.Provider>
    )
}
